INSERT INTO Title(titleType,title,startYear,endYear,RuntimeMinutes,link,views,avgRating,poster_link,overview) values('movie', 'Citizen Kane', '1941', NULL, 119, 'https://youtu.be/8dxh3lwdOFw', 0, 0, 'https://upload.wikimedia.org/wikipedia/commons/c/c0/Citizen_Kane_poster%2C_1941_%28Style_B%2C_unrestored%29.jpg', 'Citizen Kane is a 1941 American drama film directed by, produced by, and starring Orson Welles. He also co-wrote the screenplay with Herman J. Mankiewicz. The picture was Welles first feature film. Citizen Kane is frequently cited as the greatest film ever made.');
INSERT INTO Title(titleType,title,startYear,endYear,RuntimeMinutes,link,views,avgRating,poster_link,overview) values('TVseries', 'The Last of Us', '2023', NULL, 50, 'https://youtu.be/uLtkt8BonwM', 0, 0, 'https://i0.wp.com/bloody-disgusting.com/wp-content/uploads/2022/11/last-of-us-tv-1.png?ssl=1','The Last of Us is an American post-apocalyptic drama television series created by Craig Mazin and Neil Druckmann for HBO. Based on the 2013 video game developed by Naughty Dog, the series is set in 2023, twenty years into a pandemic caused by a mass fungal infection, which causes its hosts to transform into zombie-like creatures and collapses society.');
INSERT INTO Title(titleType,title,startYear,endYear,RuntimeMinutes,link,views,avgRating,poster_link,overview) values('movie', 'Barbie', '2023', NULL, NULL, 'https://youtu.be/GRyt3Ov4zz0', 0, 0,'https://en.wikipedia.org/wiki/Barbie_(film)#/media/File:Barbie_2023_poster.jpg','Barbie is an upcoming American fantasy[2] comedy film directed by Greta Gerwig, who co-wrote it with Noah Baumbach. Based on the Barbie fashion dolls by Mattel, the film is the first live-action Barbie film after several computer-animated direct-to-video and streaming television films.');
INSERT INTO Title(titleType,title,startYear,endYear,RuntimeMinutes,link,views,avgRating,poster_link,overview) values('TVseries', 'Breaking Bad', '2008', '2013', 49, 'https://youtu.be/HhesaQXLuRY', 0, 0,'https://en.wikipedia.org/wiki/Breaking_Bad#/media/File:Breaking_Bad_logo.svg','Breaking Bad is an American neo-Western crime drama television series created and produced by Vince Gilligan for AMC. Set and filmed in Albuquerque, New Mexico, the series follows Walter White (Bryan Cranston), an underpaid, overqualified, and dispirited high-school chemistry teacher who is struggling with a recent diagnosis of stage-three lung cancer. White turns to a life of crime and partners with a former student, Jesse Pinkman (Aaron Paul), to produce and distribute methamphetamine to secure his familys financial future before he dies, while navigating the dangers of the criminal underworld.');
INSERT INTO Title(titleType,title,startYear,endYear,RuntimeMinutes,link,views,avgRating,poster_link,overview) values('movie', 'The Super Mario Bros. Movie', '2023', NULL, 92, 'https://youtu.be/TnGl01FkMMo', 0, 0,'https://en.wikipedia.org/wiki/The_Super_Mario_Bros._Movie#/media/File:The_Super_Mario_Bros._Movie_poster.jpg','The Super Mario Bros. Movie is a 2023 computer-animated adventure film based on Nintendos Mario video game franchise. Produced by Universal Pictures, Illumination, and Nintendo, and distributed by Universal, the film was directed by Aaron Horvath and Michael Jelenic and written by Matthew Fogel.');
